import { GiftFormData, GiftRecommendation, GPTResponse } from '../types/gift';

const API_URL = 'https://api.openai.com/v1/chat/completions';
const API_KEY = process.env.REACT_APP_OPENAI_API_KEY;

export const getGiftRecommendations = async (formData: GiftFormData): Promise<GPTResponse> => {
  if (!API_KEY) {
    throw new Error('OpenAI API ÌÇ§Í∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§.');
  }

  const prompt = `
ÎãπÏã†ÏùÄ Ïª§Ìîå Í∏∞ÎÖêÏùº ÏÑ†Î¨º Ï∂îÏ≤ú Ï†ÑÎ¨∏Í∞ÄÏûÖÎãàÎã§. Îã§Ïùå Ï†ïÎ≥¥Î•º Î∞îÌÉïÏúºÎ°ú 3-4Í∞úÏùò ÏÑ†Î¨ºÏùÑ Ï∂îÏ≤úÌï¥Ï£ºÏÑ∏Ïöî.

ÏÉÅÎåÄÎ∞© Ï†ïÎ≥¥:
- ÏÑ±Î≥Ñ: ${formData.gender === 'male' ? 'ÎÇ®ÏÑ±' : 'Ïó¨ÏÑ±'}
- ÎÇòÏù¥: ${formData.age}ÏÑ∏
- ÏÑ±Í≤©/Ï∑®Ìñ•: ${formData.personality}
- Í∏∞ÎÖêÏùº: ${formData.occasionType}
- ÏòàÏÇ∞: ${formData.minBudget.toLocaleString()}Ïõê ~ ${formData.maxBudget.toLocaleString()}Ïõê
- ÏÑ†Ìò∏ Ïπ¥ÌÖåÍ≥†Î¶¨: ${formData.category || 'Ï†ÑÏ≤¥'}
- Ï∂îÍ∞Ä Ï†ïÎ≥¥: ${formData.additionalInfo || 'ÏóÜÏùå'}

Îã§Ïùå JSON ÌòïÏãùÏúºÎ°ú Ï†ïÌôïÌûà ÎãµÎ≥ÄÌï¥Ï£ºÏÑ∏Ïöî:
{
  "recommendations": [
    {
      "id": "1",
      "title": "ÏÑ†Î¨º Ïù¥Î¶Ñ",
      "description": "ÏÑ†Î¨ºÏóê ÎåÄÌïú Í∞ÑÎã®Ìïú ÏÑ§Î™Ö (50Ïûê Ïù¥ÎÇ¥)",
      "price": "ÏòàÏÉÅ Í∞ÄÍ≤© (Ïòà: 59,000Ïõê)",
      "category": "Ïπ¥ÌÖåÍ≥†Î¶¨",
      "searchKeyword": "Ïø†Ìå° Í≤ÄÏÉâÏö© ÌÇ§ÏõåÎìú (Íµ¨Ï≤¥Ï†ÅÏù¥Í≥† Í∞ÑÎã®ÌïòÍ≤å)"
    }
  ]
}

Ï£ºÏùòÏÇ¨Ìï≠:
- ÏòàÏÇ∞ Î≤îÏúÑ ÎÇ¥Ïùò ÌòÑÏã§Ï†ÅÏù∏ Í∞ÄÍ≤©ÏúºÎ°ú Ï∂îÏ≤ú
- ÏÑ±Î≥Ñ, ÎÇòÏù¥, ÏÑ±Í≤©ÏùÑ Í≥†Î†§Ìïú ÎßûÏ∂§Ìòï Ï∂îÏ≤ú
- Í∏∞ÎÖêÏùº ÌäπÏÑ±Ïóê ÎßûÎäî ÏùòÎØ∏ÏûàÎäî ÏÑ†Î¨º Ï†úÏïà
- searchKeywordÎäî Ïø†Ìå°ÏóêÏÑú Ïã§Ï†ú Í≤ÄÏÉâ Í∞ÄÎä•Ìïú Îã®ÏàúÌïú ÌÇ§ÏõåÎìúÎ°ú ÏûëÏÑ± (Ïòà: "Ïª§Ìîå Î™©Í±∏Ïù¥", "Î¨¥ÏÑ† Ïù¥Ïñ¥Ìè∞", "Ìñ•Ïàò")
- JSON ÌòïÏãùÏùÑ Ï†ïÌôïÌûà ÏßÄÏºúÏ£ºÏÑ∏Ïöî
`;

  try {
    console.log('ü§ñ GPT API Ìò∏Ï∂ú ÏãúÏûë...', {
      hasApiKey: !!API_KEY,
      apiKeyPrefix: API_KEY ? API_KEY.substring(0, 10) + '...' : 'ÏóÜÏùå'
    });

    const response = await fetch(API_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${API_KEY}`
      },
      body: JSON.stringify({
        model: 'gpt-4o-mini',
        messages: [
          {
            role: 'system',
            content: 'ÎãπÏã†ÏùÄ Ïª§Ìîå Í∏∞ÎÖêÏùº ÏÑ†Î¨º Ï∂îÏ≤ú Ï†ÑÎ¨∏Í∞ÄÏûÖÎãàÎã§. ÏÇ¨Ïö©ÏûêÏùò ÏöîÍµ¨ÏÇ¨Ìï≠ÏùÑ Î∂ÑÏÑùÌïòÏó¨ ÏµúÏ†ÅÏùò ÏÑ†Î¨ºÏùÑ JSON ÌòïÏãùÏúºÎ°ú Ï∂îÏ≤úÌï¥Ï£ºÏÑ∏Ïöî.'
          },
          {
            role: 'user',
            content: prompt
          }
        ],
        max_tokens: 1000,
        temperature: 0.7
      })
    });

    console.log('üì° GPT API ÏùëÎãµ:', {
      status: response.status,
      statusText: response.statusText,
      ok: response.ok
    });

    if (!response.ok) {
      const errorData = await response.text();
      console.error('‚ùå GPT API Ïò§Î•ò ÏÉÅÏÑ∏:', {
        status: response.status,
        statusText: response.statusText,
        errorData: errorData
      });
      throw new Error(`API ÏöîÏ≤≠ Ïã§Ìå®: ${response.status} - ${errorData}`);
    }

    const data = await response.json();
    console.log('‚úÖ GPT API ÏÑ±Í≥µÏ†Å ÏùëÎãµ Î∞õÏùå');
    
    const gptResponse = data.choices[0].message.content;
    console.log('üìù GPT ÏùëÎãµ ÎÇ¥Ïö©:', gptResponse);
    
    // JSON ÌååÏã±
    const parsedResponse = JSON.parse(gptResponse);
    console.log('üîÑ ÌååÏã±Îêú ÏùëÎãµ:', parsedResponse);
    
    // Í∞Å Ï∂îÏ≤ú ÏÉÅÌíàÏóê ÎåÄÌï¥ Ïø†Ìå° Í≤ÄÏÉâ ÎßÅÌÅ¨ ÏÉùÏÑ±
    const recommendationsWithLinks = parsedResponse.recommendations.map((rec: any, index: number) => {
      const searchKeyword = rec.searchKeyword || rec.title;
      const coupangUrl = generateCoupangSearchLink(searchKeyword);
      
      // Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ Í∏∞Î≥∏ Ïù¥ÎØ∏ÏßÄ ÏÑ§Ï†ï
      const getDefaultImage = (category: string) => {
        const categoryImages: { [key: string]: string } = {
          'Ïï°ÏÑ∏ÏÑúÎ¶¨': 'https://images.unsplash.com/photo-1515562141207-7a88fb7ce338?w=300&h=200&fit=crop',
          'Î∑∞Ìã∞': 'https://images.unsplash.com/photo-1596462502278-27bfdc403348?w=300&h=200&fit=crop',
          'Ìñ•Ïàò': 'https://images.unsplash.com/photo-1541643600914-78b084683601?w=300&h=200&fit=crop',
          'ITÍ∏∞Í∏∞': 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=300&h=200&fit=crop',
          'Ìå®ÏÖò': 'https://images.unsplash.com/photo-1445205170230-053b83016050?w=300&h=200&fit=crop',
          'ÏÉùÌôúÏö©Ìíà': 'https://images.unsplash.com/photo-1586880244386-8b3e34734ed8?w=300&h=200&fit=crop',
          'ÍΩÉ': 'https://images.unsplash.com/photo-1490750967868-88aa4486c946?w=300&h=200&fit=crop',
          'ÏùåÏãù': 'https://images.unsplash.com/photo-1567620905732-2d1ec7ab7445?w=300&h=200&fit=crop',
          'Í∏∞Î≥∏': 'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?w=300&h=200&fit=crop'
        };
        
        return categoryImages[category] || categoryImages['Í∏∞Î≥∏'];
      };
      
      // ÎîîÎ≤ÑÍπÖÏùÑ ÏúÑÌïú Î°úÍ∑∏
      console.log(`üîç Ï∂îÏ≤ú ${index + 1}:`, {
        title: rec.title,
        searchKeyword: searchKeyword,
        coupangUrl: coupangUrl
      });
      
      return {
        id: rec.id,
        title: rec.title,
        description: rec.description,
        price: rec.price,
        imageUrl: getDefaultImage(rec.category),
        coupangUrl: coupangUrl,
        category: rec.category,
        rating: 4.5, // Í∏∞Î≥∏ ÌèâÏ†ê
        reviewCount: Math.floor(Math.random() * 500) + 50 // ÎûúÎç§ Î¶¨Î∑∞ Ïàò
      };
    });

    return {
      recommendations: recommendationsWithLinks,
      success: true
    };
  } catch (error) {
    console.error('üí• GPT API Ï†ÑÏ≤¥ Ïò§Î•ò:', error);
    
    if (error instanceof Error) {
      console.error('üîç ÏóêÎü¨ ÏÉÅÏÑ∏:', {
        name: error.name,
        message: error.message,
        stack: error.stack
      });
    }
    
    return {
      recommendations: [],
      success: false,
      error: error instanceof Error ? error.message : 'Ïïå Ïàò ÏóÜÎäî Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.'
    };
  }
};

// Ïø†Ìå° ÌååÌä∏ÎÑàÏä§ Í≤ÄÏÉâ ÎßÅÌÅ¨ ÏÉùÏÑ± Ìï®Ïàò
const generateCoupangSearchLink = (keyword: string): string => {
  const partnerId = process.env.REACT_APP_COUPANG_PARTNER_ID || '';
  const encodedKeyword = encodeURIComponent(keyword);
  
  console.log(`üîó ÎßÅÌÅ¨ ÏÉùÏÑ± ÏãúÎèÑ:`, {
    keyword: keyword,
    partnerId: partnerId ? `${partnerId.substring(0, 10)}...` : 'ÏóÜÏùå',
    encodedKeyword: encodedKeyword
  });
  
  if (partnerId) {
    // ÌååÌä∏ÎÑà IDÍ∞Ä ÏûàÏúºÎ©¥ ÌååÌä∏ÎÑàÏä§ ÎßÅÌÅ¨ ÏÉùÏÑ±
    const partnerLink = `https://link.coupang.com/a/${partnerId}?url=https%3A%2F%2Fwww.coupang.com%2Fnp%2Fsearch%3Fq%3D${encodedKeyword}`;
    console.log(`‚úÖ ÌååÌä∏ÎÑàÏä§ ÎßÅÌÅ¨ ÏÉùÏÑ±:`, partnerLink);
    return partnerLink;
  } else {
    // ÌååÌä∏ÎÑà IDÍ∞Ä ÏóÜÏúºÎ©¥ ÏùºÎ∞ò Í≤ÄÏÉâ ÎßÅÌÅ¨ ÏÉùÏÑ±
    const searchLink = `https://www.coupang.com/np/search?q=${encodedKeyword}`;
    console.log(`‚úÖ ÏùºÎ∞ò Í≤ÄÏÉâ ÎßÅÌÅ¨ ÏÉùÏÑ±:`, searchLink);
    return searchLink;
  }
};

// ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÉùÏÑ± Ìï®Ïàò (Í∞úÎ∞ú/ÌÖåÏä§Ìä∏Ïö©)
