    try {
      // Ìï≠ÏÉÅ Ïã§Ï†ú API Ìò∏Ï∂úÏùÑ ÏãúÎèÑÌïòÍ≥†, Ïã§Ìå® ÏãúÏóêÎßå ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
      console.log('üöÄ API Ìò∏Ï∂ú ÏãúÏûë...');
      
      const response = await getGiftRecommendations(formData);
      
      if (response.success) {
        setRecommendations(response.recommendations);
        console.log('‚úÖ API Ìò∏Ï∂ú ÏÑ±Í≥µ:', response.recommendations.length, 'Í∞ú Ï∂îÏ≤ú');
      } else {
        console.warn('‚ö†Ô∏è API Ìò∏Ï∂ú Ïã§Ìå®, ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©:', response.error);
        // API Ìò∏Ï∂ú Ïã§Ìå® Ïãú ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
        const dummyResponse = await getDummyRecommendations(formData);
        setRecommendations(dummyResponse.recommendations);
        setError('API Ìò∏Ï∂úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. ÏÉòÌîå Îç∞Ïù¥ÌÑ∞Î•º ÌëúÏãúÌï©ÎãàÎã§.');
      }
    } catch (err) {
      console.error('üí• Ï†ÑÏ≤¥ Ìò∏Ï∂ú Ïã§Ìå®:', err);
      try {
        // ÏôÑÏ†Ñ Ïã§Ìå® Ïãú ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©
        const dummyResponse = await getDummyRecommendations(formData);
        setRecommendations(dummyResponse.recommendations);
        setError('ÏÑúÎ≤Ñ Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. ÏÉòÌîå Îç∞Ïù¥ÌÑ∞Î•º ÌëúÏãúÌï©ÎãàÎã§.');
      } catch (dummyErr) {
        setError('Ï∂îÏ≤úÏùÑ Î∞õÏïÑÏò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleRegenerate = async () => {
    if (!currentFormData) return;
    
    setLoading(true);
    setError(null);
    
    try {
      console.log('üîÑ Ïû¨Ï∂îÏ≤ú ÏãúÏûë...');
      
      const response = await getGiftRecommendations(currentFormData);
      
      if (response.success) {
        setRecommendations(response.recommendations);
        console.log('‚úÖ Ïû¨Ï∂îÏ≤ú ÏÑ±Í≥µ:', response.recommendations.length, 'Í∞ú Ï∂îÏ≤ú');
      } else {
        console.warn('‚ö†Ô∏è Ïû¨Ï∂îÏ≤ú Ïã§Ìå®, ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©:', response.error);
        const dummyResponse = await getDummyRecommendations(currentFormData);
        setRecommendations(dummyResponse.recommendations);
        setError('API Ìò∏Ï∂úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. ÏÉòÌîå Îç∞Ïù¥ÌÑ∞Î•º ÌëúÏãúÌï©ÎãàÎã§.');
      }
    } catch (err) {
      console.error('üí• Ïû¨Ï∂îÏ≤ú Ïã§Ìå®:', err);
      try {
        const dummyResponse = await getDummyRecommendations(currentFormData);
        setRecommendations(dummyResponse.recommendations);
        setError('ÏÑúÎ≤Ñ Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§. ÏÉòÌîå Îç∞Ïù¥ÌÑ∞Î•º ÌëúÏãúÌï©ÎãàÎã§.');
      } catch (dummyErr) {
        setError('Ï∂îÏ≤úÏùÑ Î∞õÏïÑÏò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleBackToForm = () => {
    setRecommendations([]);
    setError(null);
    setCurrentFormData(null);
  };

  return (
    <div className="min-h-screen instagram-gradient relative">
      {/* ÌîåÎ°úÌåÖ Î∞∞Í≤Ω ÏöîÏÜåÎì§ */}
      <div className="floating-bg">
        <div className="floating-element"></div>
        <div className="floating-element"></div>
        <div className="floating-element"></div>
        <div className="floating-element"></div>
        <div className="floating-element"></div>
      </div>

      <div className="container mx-auto px-4 py-8 relative z-10">
        <header className="text-center mb-12 fade-in">
          <div className="mb-6">
            <h1 className="text-5xl md:text-6xl font-bold gradient-text mb-4">
              ‚ú® ÏÑ†Î¨ºÏßÄÎãà
            </h1>
            <div className="text-2xl md:text-3xl font-semibold text-white mb-2">
              GiftGenie
            </div>
          </div>
          <p className="text-xl md:text-2xl text-white/90 font-medium mb-4">
            AIÍ∞Ä Ï∂îÏ≤úÌïòÎäî ÌäπÎ≥ÑÌïú ÏÑ†Î¨ºÏùÑ Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî
          </p>
          <div className="max-w-3xl mx-auto">
            <p className="text-lg text-white/80 leading-relaxed">
              üíë Ïª§Ìîå ‚Ä¢ üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Î∂ÄÎ∂Ä ‚Ä¢ üë∂ Ï≤≠ÏÜåÎÖÑ ‚Ä¢ üßë‚Äçüíº 20-30ÎåÄ ‚Ä¢ üë®‚Äçüíº 40-50ÎåÄ Ï§ëÎÖÑÏ∏µ ‚Ä¢ üë®‚Äçü¶≥ 60-70ÎåÄ Ïû•ÎÖÑÏ∏µ
              <br />
              <span className="text-base">Î™®Îì† Ïó∞Î†πÎåÄÏôÄ Í¥ÄÍ≥ÑÎ•º ÏúÑÌïú ÎßûÏ∂§Ìòï ÏÑ†Î¨º Ï∂îÏ≤ú ÏÑúÎπÑÏä§</span>
            </p>
          </div>
        </header>

        <div className="max-w-4xl mx-auto">
          {!loading && recommendations.length === 0 && (
            <div className="fade-in">
              <GiftForm onSubmit={handleFormSubmit} isLoading={loading} />
            </div>
          )}
          
          {loading && (
            <div className="fade-in">
              <LoadingSpinner />
            </div>
          )}
          
          {error && (
            <div className="card bg-red-50/20 border-red-200/30 text-red-100 text-center mb-6 fade-in">
              <div className="text-2xl mb-4">üòû</div>
              <p className="mb-4 text-lg">{error}</p>
              <button
                onClick={handleBackToForm}
                className="btn-primary"
              >
                üîÑ Îã§Ïãú ÏãúÎèÑÌïòÍ∏∞
              </button>
            </div>
          )}
          
          {recommendations.length > 0 && (
            <div className="fade-in">
              <GiftRecommendations 
                recommendations={recommendations} 
                onRegenerate={handleRegenerate}
                onBackToForm={handleBackToForm}
              />
            </div>
          )}
        </div>
        
        {/* Ìë∏ÌÑ∞ ÏÉÅÎã® AdSense Í¥ëÍ≥† */}
        <div className="mt-16 max-w-4xl mx-auto fade-in">
          <AdSense 
            adSlot="FOOTER_TOP_AD_SLOT"
            adFormat="banner"
            className="mb-8"
          />
        </div>
        
        <footer className="text-center mt-20 text-white/70 fade-in">
          <div className="glass-card max-w-2xl mx-auto mb-8">
            <div className="flex items-center justify-center gap-4 mb-4">
              <span className="text-3xl">‚ú®</span>
              <h3 className="text-xl font-semibold gradient-text">ÏÑ†Î¨ºÏßÄÎãà GiftGenie</h3>
              <span className="text-3xl">‚ú®</span>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
              <div className="flex items-center justify-center gap-2">
                <span className="text-lg">üéØ</span>
                <span>AI ÎßûÏ∂§ Ï∂îÏ≤ú</span>
              </div>
              <div className="flex items-center justify-center gap-2">
                <span className="text-lg">üíù</span>
                <span>Î™®Îì† Ïó∞Î†πÎåÄ</span>
              </div>
              <div className="flex items-center justify-center gap-2">
                <span className="text-lg">üõçÔ∏è</span>
                <span>Ïø†Ìå° Ïó∞Îèô</span>
              </div>
            </div>
          </div>
          <p className="mb-2">¬© 2024 ÏÑ†Î¨ºÏßÄÎãà GiftGenie. AI Í∏∞Î∞ò ÎßûÏ∂§Ìòï ÏÑ†Î¨º Ï∂îÏ≤ú ÏÑúÎπÑÏä§</p>
          <p className="text-sm opacity-70">
            Ïø†Ìå° ÌååÌä∏ÎÑàÏä§ ÌôúÎèôÏùÑ ÌÜµÌï¥ ÏùºÏ†ïÏï°Ïùò ÏàòÏàòÎ£åÎ•º Ï†úÍ≥µÎ∞õÏùÑ Ïàò ÏûàÏäµÎãàÎã§.
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
