    }
    
    return (
      <>
        <option value="">ë‚˜ì´ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”</option>
        <optgroup label="ğŸ‘¶ ì–´ë¦°ì´ (10-12ì„¸)">
          {childrenOptions}
        </optgroup>
        <optgroup label="ğŸ‘¦ ì²­ì†Œë…„ (13-19ì„¸)">
          {teenOptions}
        </optgroup>
        <optgroup label="ğŸ§‘â€ğŸ’¼ ì²­ë…„ì¸µ (20-39ì„¸)">
          {youngAdultOptions}
        </optgroup>
        <optgroup label="ğŸ‘¨â€ğŸ’¼ ì¤‘ë…„ì¸µ (40-59ì„¸)">
