              <input
                type="radio"
                name="gender"
                value="male"
                checked={formData.gender === 'male'}
                onChange={handleInputChange}
                className="sr-only"
              />
              <span className="text-xl">ğŸ‘¨</span>
              <span>ë‚¨ì„±</span>
            </label>
          </div>
