import React, { useState } from 'react';
import { GiftFormData } from '../types/gift';

interface GiftFormProps {
  onSubmit: (formData: GiftFormData) => void;
  isLoading?: boolean;
}

const GiftForm: React.FC<GiftFormProps> = ({ onSubmit, isLoading }) => {
  // ÌôòÍ≤Ω Î≥ÄÏàò ÎîîÎ≤ÑÍπÖ Ï†ïÎ≥¥ Ï∂îÍ∞Ä
  React.useEffect(() => {
    const hasOpenAIKey = !!process.env.REACT_APP_OPENAI_API_KEY;
    const hasCoupangPartnerId = !!process.env.REACT_APP_COUPANG_PARTNER_ID;
    const hasAdSenseId = !!process.env.REACT_APP_ADSENSE_PUBLISHER_ID;
    
    console.log('üîç ÌôòÍ≤Ω Î≥ÄÏàò ÏÉÅÌÉú ÌôïÏù∏:', {
      openAIKey: hasOpenAIKey ? '‚úÖ ÏÑ§Ï†ïÎê®' : '‚ùå ÏóÜÏùå',
      coupangPartnerId: hasCoupangPartnerId ? '‚úÖ ÏÑ§Ï†ïÎê®' : '‚ö†Ô∏è ÏóÜÏùå (ÏùºÎ∞ò ÎßÅÌÅ¨ ÏÇ¨Ïö©)',
      adSenseId: hasAdSenseId ? '‚úÖ ÏÑ§Ï†ïÎê®' : '‚ö†Ô∏è ÏóÜÏùå (Í¥ëÍ≥† ÎπÑÌôúÏÑ±Ìôî)',
      nodeEnv: process.env.NODE_ENV || 'development'
    });
    
    if (!hasOpenAIKey) {
      console.warn('‚ö†Ô∏è OpenAI API ÌÇ§Í∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§. ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞Í∞Ä ÌëúÏãúÎê† Ïàò ÏûàÏäµÎãàÎã§.');
    }
    
    if (!hasCoupangPartnerId) {
      console.info('üí° Ïø†Ìå° ÌååÌä∏ÎÑàÏä§ IDÍ∞Ä ÏÑ§Ï†ïÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§. ÏùºÎ∞ò Ïø†Ìå° Í≤ÄÏÉâ ÎßÅÌÅ¨Î•º ÏÇ¨Ïö©Ìï©ÎãàÎã§.');
    }
  }, []);

  const [formData, setFormData] = useState<GiftFormData>({
    gender: 'female',
    age: 25,
    personality: '',
    occasionType: '',
    minBudget: 30000,
    maxBudget: 100000,
    category: '',
    additionalInfo: ''
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'age' || name === 'minBudget' || name === 'maxBudget' 
        ? Number(value) 
        : value
    }));
  };

  // ÎÇòÏù¥ ÏòµÏÖò ÏÉùÏÑ± Ìï®Ïàò
  const generateAgeOptions = () => {
    // Ïñ¥Î¶∞Ïù¥ (10-12ÏÑ∏)
    const childrenOptions = [];
    for (let age = 10; age <= 12; age++) {
      childrenOptions.push(
        <option key={age} value={age}>{age}ÏÑ∏</option>
      );
    }
    
    // Ï≤≠ÏÜåÎÖÑ (13-19ÏÑ∏)
    const teenOptions = [];
    for (let age = 13; age <= 19; age++) {
      teenOptions.push(
        <option key={age} value={age}>{age}ÏÑ∏</option>
      );
    }
    
    // Ï≤≠ÎÖÑÏ∏µ (20-39ÏÑ∏)
    const youngAdultOptions = [];
    for (let age = 20; age <= 39; age++) {
      youngAdultOptions.push(
        <option key={age} value={age}>{age}ÏÑ∏</option>
      );
    }
    
    // Ï§ëÎÖÑÏ∏µ (40-59ÏÑ∏)
    const middleAgedOptions = [];
    for (let age = 40; age <= 59; age++) {
      middleAgedOptions.push(
        <option key={age} value={age}>{age}ÏÑ∏</option>
      );
    }
    
    // Ïû•ÎÖÑÏ∏µ (60-70ÏÑ∏)
    const elderlyOptions = [];
    for (let age = 60; age <= 70; age++) {
      elderlyOptions.push(
        <option key={age} value={age}>{age}ÏÑ∏</option>
      );
    }
    
    return (
      <>
        <option value="">ÎÇòÏù¥Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî</option>
        <optgroup label="üë∂ Ïñ¥Î¶∞Ïù¥ (10-12ÏÑ∏)">
          {childrenOptions}
        </optgroup>
        <optgroup label="üë¶ Ï≤≠ÏÜåÎÖÑ (13-19ÏÑ∏)">
          {teenOptions}
        </optgroup>
        <optgroup label="üßë‚Äçüíº Ï≤≠ÎÖÑÏ∏µ (20-39ÏÑ∏)">
          {youngAdultOptions}
        </optgroup>
        <optgroup label="üë®‚Äçüíº Ï§ëÎÖÑÏ∏µ (40-59ÏÑ∏)">
          {middleAgedOptions}
        </optgroup>
        <optgroup label="üë®‚Äçü¶≥ Ïû•ÎÖÑÏ∏µ (60-70ÏÑ∏)">
          {elderlyOptions}
        </optgroup>
      </>
    );
  };

  return (
    <div className="card max-w-2xl mx-auto">
      <div className="text-center mb-8">
        <h2 className="text-3xl font-bold gradient-text mb-4">
          ÏÑ†Î¨º Î∞õÏùÑ Î∂ÑÏùò Ï†ïÎ≥¥Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî
        </h2>
        <p className="text-gray-600 text-lg">
          ÏûêÏÑ∏Ìïú Ï†ïÎ≥¥Î•º Ï†úÍ≥µÌï†ÏàòÎ°ù Îçî Ï†ïÌôïÌïú Ï∂îÏ≤úÏùÑ Î∞õÏùÑ Ïàò ÏûàÏñ¥Ïöî! ‚ú®
        </p>
      </div>
      
      <form onSubmit={handleSubmit} className="space-y-8">
        {/* ÏÑ±Î≥Ñ */}
        <div>
          <label className="form-label">
            <span className="text-2xl">üë§</span>
            ÏÑ±Î≥Ñ
          </label>
          <div className="grid grid-cols-2 gap-4">
            <label className={`radio-option ${formData.gender === 'female' ? 'selected' : ''}`}>
              <input
                type="radio"
                name="gender"
                value="female"
                checked={formData.gender === 'female'}
                onChange={handleInputChange}
                className="sr-only"
              />
              <span className="text-xl">üë©</span>
              <span>Ïó¨ÏÑ±</span>
            </label>
            <label className={`radio-option ${formData.gender === 'male' ? 'selected' : ''}`}>
              <input
                type="radio"
                name="gender"
                value="male"
