import React, { useState } from 'react';
import GiftForm from './components/GiftForm';
import GiftRecommendations from './components/GiftRecommendations';
import LoadingSpinner from './components/LoadingSpinner';
import AdSense from './components/AdSense';
import { GiftFormData, GiftRecommendation } from './types/gift';
import { getGiftRecommendations, getDummyRecommendations } from './services/gptService';

function App() {
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState<GiftRecommendation[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [currentFormData, setCurrentFormData] = useState<GiftFormData | null>(null);

  const handleFormSubmit = async (formData: GiftFormData) => {
    setLoading(true);
    setError(null);
    setCurrentFormData(formData);
    
    try {
      // Í∞úÎ∞ú ÌôòÍ≤ΩÏóêÏÑúÎäî ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ¨Ïö©, ÌîÑÎ°úÎçïÏÖòÏóêÏÑúÎäî Ïã§Ï†ú GPT API ÏÇ¨Ïö©
      const isDevelopment = !process.env.REACT_APP_OPENAI_API_KEY;
      
      const response = isDevelopment 
        ? await getDummyRecommendations(formData)
        : await getGiftRecommendations(formData);
      
      if (response.success) {
        setRecommendations(response.recommendations);
      } else {
        setError(response.error || 'Ï∂îÏ≤úÏùÑ Î∞õÏïÑÏò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    } catch (err) {
      setError('Ï∂îÏ≤úÏùÑ Î∞õÏïÑÏò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    } finally {
      setLoading(false);
    }
  };

  const handleRegenerate = async () => {
    if (!currentFormData) return;
    
    setLoading(true);
    setError(null);
    
    try {
      const isDevelopment = !process.env.REACT_APP_OPENAI_API_KEY;
      
      const response = isDevelopment 
        ? await getDummyRecommendations(currentFormData)
        : await getGiftRecommendations(currentFormData);
      
      if (response.success) {
        setRecommendations(response.recommendations);
      } else {
        setError(response.error || 'Ï∂îÏ≤úÏùÑ Î∞õÏïÑÏò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    } catch (err) {
      setError('Ï∂îÏ≤úÏùÑ Î∞õÏïÑÏò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
    } finally {
      setLoading(false);
    }
  };

  const handleBackToForm = () => {
    setRecommendations([]);
    setError(null);
    setCurrentFormData(null);
  };

  return (
    <div className="min-h-screen instagram-gradient relative">
      {/* ÌîåÎ°úÌåÖ Î∞∞Í≤Ω ÏöîÏÜåÎì§ */}
      <div className="floating-bg">
        <div className="floating-element"></div>
        <div className="floating-element"></div>
        <div className="floating-element"></div>
        <div className="floating-element"></div>
        <div className="floating-element"></div>
      </div>

      <div className="container mx-auto px-4 py-8 relative z-10">
        <header className="text-center mb-12 fade-in">
          <div className="mb-6">
            <h1 className="text-5xl md:text-6xl font-bold gradient-text mb-4">
              ‚ú® ÏÑ†Î¨ºÏßÄÎãà
            </h1>
            <div className="text-2xl md:text-3xl font-semibold text-white mb-2">
              GiftGenie
            </div>
          </div>
          <p className="text-xl md:text-2xl text-white/90 font-medium mb-4">
            AIÍ∞Ä Ï∂îÏ≤úÌïòÎäî ÌäπÎ≥ÑÌïú ÏÑ†Î¨ºÏùÑ Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî
          </p>
          <div className="max-w-3xl mx-auto">
            <p className="text-lg text-white/80 leading-relaxed">
              üíë Ïª§Ìîå ‚Ä¢ üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Î∂ÄÎ∂Ä ‚Ä¢ üë∂ Ï≤≠ÏÜåÎÖÑ ‚Ä¢ üßë‚Äçüíº 20-30ÎåÄ ‚Ä¢ üë®‚Äçü¶≥ 40-60ÎåÄ Ï§ëÏû•ÎÖÑÏ∏µ
              <br />
              <span className="text-base">Î™®Îì† Ïó∞Î†πÎåÄÏôÄ Í¥ÄÍ≥ÑÎ•º ÏúÑÌïú ÎßûÏ∂§Ìòï ÏÑ†Î¨º Ï∂îÏ≤ú ÏÑúÎπÑÏä§</span>
            </p>
          </div>
        </header>

        <div className="max-w-4xl mx-auto">
          {!loading && recommendations.length === 0 && (
            <div className="fade-in">
              <GiftForm onSubmit={handleFormSubmit} />
            </div>
          )}
          
          {loading && (
            <div className="fade-in">
              <LoadingSpinner />
            </div>
          )}
          
          {error && (
            <div className="card bg-red-50/20 border-red-200/30 text-red-100 text-center mb-6 fade-in">
              <div className="text-2xl mb-4">üòû</div>
              <p className="mb-4 text-lg">{error}</p>
              <button
                onClick={handleBackToForm}
                className="btn-primary"
              >
                üîÑ Îã§Ïãú ÏãúÎèÑÌïòÍ∏∞
              </button>
            </div>
          )}
          
          {recommendations.length > 0 && (
            <div className="fade-in">
              <GiftRecommendations 
                recommendations={recommendations} 
                onRegenerate={handleRegenerate}
                onBackToForm={handleBackToForm}
              />
            </div>
          )}
        </div>
        
        {/* Ìë∏ÌÑ∞ ÏÉÅÎã® AdSense Í¥ëÍ≥† */}
        <div className="mt-16 max-w-4xl mx-auto fade-in">
          <AdSense 
            adSlot="FOOTER_TOP_AD_SLOT"
            adFormat="banner"
            className="mb-8"
          />
        </div>
        
        <footer className="text-center mt-20 text-white/70 fade-in">
          <div className="glass-card max-w-2xl mx-auto mb-8">
            <div className="flex items-center justify-center gap-4 mb-4">
              <span className="text-3xl">‚ú®</span>
              <h3 className="text-xl font-semibold gradient-text">ÏÑ†Î¨ºÏßÄÎãà GiftGenie</h3>
              <span className="text-3xl">‚ú®</span>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
              <div className="flex items-center justify-center gap-2">
                <span className="text-lg">üéØ</span>
                <span>AI ÎßûÏ∂§ Ï∂îÏ≤ú</span>
              </div>
