import React, { useEffect } from 'react';

declare global {
  interface Window {
    adsbygoogle: any;
  }
}

interface AdSenseProps {
  adSlot: string;
  adFormat?: 'auto' | 'rectangle' | 'banner';
  adStyle?: React.CSSProperties;
  className?: string;
}

const AdSense: React.FC<AdSenseProps> = ({ 
  adSlot, 
  adFormat = 'auto', 
  adStyle,
  className = ''
}) => {
  useEffect(() => {
    // AdSense 스크립트가 로드되었는지 확인
    if (window.adsbygoogle) {
      try {
        window.adsbygoogle.push({});
      } catch (err) {
        console.error('AdSense push error:', err);
      }
    }

    // 클린업 함수 (컴포넌트가 사라질 때 호출)
    return () => {
      // 컴포넌트가 언마운트될 때 광고 상태를 정리하는 로직 (필요 시)
      // 현재는 특별한 정리 작업이 필요하지 않음
    };
  }, [adSlot]); // adSlot이 변경될 때만 effect를 다시 실행

  // Publisher ID가 설정되지 않았으면 광고를 렌더링하지 않음 (로컬 에러 방지)
  if (!process.env.REACT_APP_ADSENSE_PUBLISHER_ID) {
    return (
      <div className={`adsense-placeholder ${className}`} style={adStyle}>
        광고 영역 (Publisher ID 미설정)
      </div>
    );
  }

  return (
    <div className={`adsense-container ${className}`}>
      <ins
        key={adSlot} // React가 요소를 식별할 수 있도록 key 추가
        className="adsbygoogle"
        style={{
          display: 'block',
          textAlign: 'center',
          ...adStyle
        }}
        data-ad-client={process.env.REACT_APP_ADSENSE_PUBLISHER_ID}
        data-ad-slot={adSlot}
        data-ad-format={adFormat}
        data-full-width-responsive="true"
      />
    </div>
  );
};

export default AdSense;
